import React, { useState, useEffect } from 'react';
import {
  FiUsers, FiFilm, FiMapPin, FiDollarSign, FiTrendingUp, FiPlus, FiSearch, FiTrash2,
  FiCheck, FiX, FiShield, FiUserMinus, FiEdit
} from 'react-icons/fi';
import api from '../../services/api';
import { toast } from 'react-hot-toast';
import { useAuth } from '../../context/AuthContext';

const SuperAdminDashboard = () => {
  const { user } = useAuth();

  // Top metrics
  const [stats, setStats] = useState({
    totalUsers: 0,
    totalTheaters: 0,
    totalMovies: 0,
    totalBookings: 0,
    totalRevenue: 0,
    monthlyRevenue: []
  });
  const [recentActivity, setRecentActivity] = useState([]);
  const [loading, setLoading] = useState(true);

  // Theaters (pending approvals for theater admin requests)
  const [pendingRequests, setPendingRequests] = useState([]); // Changed to pending theater admin requests
  const [approvingId, setApprovingId] = useState(null);

  // Users admin
  const [users, setUsers] = useState([]);
  const [userPagination, setUserPagination] = useState({ currentPage: 1, totalPages: 1, totalUsers: 0, hasNext: false, hasPrev: false });
  const [userFilters, setUserFilters] = useState({ search: '', role: '', isActive: '' });
  const [userBusyId, setUserBusyId] = useState(null);
  const [showUserModal, setShowUserModal] = useState(false);
  const [userForm, setUserForm] = useState({ name: '', email: '', password: '', role: 'user' });

  // Movies admin
  const [movies, setMovies] = useState([]);
  const [moviePagination, setMoviePagination] = useState({ currentPage: 1, totalPages: 1, totalMovies: 0, hasNext: false, hasPrev: false });
  const [movieSearch, setMovieSearch] = useState('');
  const [movieBusyId, setMovieBusyId] = useState(null);

  // All theaters for super admin management
  const [allTheaters, setAllTheaters] = useState([]);
  const [theaterPagination, setTheaterPagination] = useState({ currentPage: 1, totalPages: 1, totalTheaters: 0, hasNext: false, hasPrev: false });
  const [theaterFilters, setTheaterFilters] = useState({ search: '', city: '' });
  const [theaterBusyId, setTheaterBusyId] = useState(null);
  const [showTheaterModal, setShowTheaterModal] = useState(false);
  const [editingTheater, setEditingTheater] = useState(null);
  const [theaterForm, setTheaterForm] = useState({
    name: '',
    address: { street: '', city: '', state: '', pincode: '' },
    location: { type: 'Point', coordinates: ['', ''] },
    facilities: [],
    description: ''
  });

  const [activeTab, setActiveTab] = useState('overview');

  useEffect(() => {
    fetchOverview();
    fetchPendingRequests();
  }, []);

  useEffect(() => {
    if (activeTab === 'users') fetchUsers(1);
    if (activeTab === 'movies') fetchMovies(1);
    if (activeTab === 'theaters') fetchAllTheaters(1);
  }, [activeTab]);

  // Overview and activity; tolerant if endpoints aren’t present
  const fetchOverview = async () => {
    setLoading(true);
    try {
      const [statsRes, activityRes] = await Promise.allSettled([
        api.get('/admin/stats'),
        api.get('/admin/recent-activity')
      ]);
      if (statsRes.status === 'fulfilled') setStats(statsRes.value.data.data || {});
      if (activityRes.status === 'fulfilled') setRecentActivity(activityRes.value.data.data?.activities || []);
    } catch (err) {
      // soft-fail
      console.error('Overview load error', err);
    } finally {
      setLoading(false);
    }
  };

  // Pending theaters awaiting approval (super_admin)
  const fetchPendingRequests = async () => {
    try {
      const res = await api.get('/theaters/admin/pending');
      setPendingRequests(res.data.data?.theaters || []);
    } catch (err) {
      console.error('Pending requests error', err);
      toast.error('Failed to load pending requests');
    }
  };

  const approveRequest = async (id) => {
    try {
      setApprovingId(id);
      await api.put(`/theaters/admin/${id}/approve`);
      toast.success('Request approved');
      await fetchPendingRequests();
    } catch (err) {
      console.error('Approve request error', err);
      toast.error('Failed to approve');
    } finally {
      setApprovingId(null);
    }
  };

  const rejectRequest = async (id) => {
    if (!window.confirm('Reject this request?')) return;
    try {
      setApprovingId(id);
      await api.put(`/theaters/admin/${id}/reject`);
      toast.success('Request rejected');
      await fetchPendingRequests();
    } catch (err) {
      console.error('Reject request error', err);
      toast.error('Failed to reject');
    } finally {
      setApprovingId(null);
    }
  };

  // Users admin
  const fetchUsers = async (page = userPagination.currentPage) => {
    try {
      const params = new URLSearchParams();
      if (userFilters.search) params.set('search', userFilters.search);
      if (userFilters.role) params.set('role', userFilters.role);
      if (userFilters.isActive !== '') params.set('isActive', userFilters.isActive);
      params.set('page', page);
      params.set('limit', 10);

      const res = await api.get(`/users/admin/users?${params.toString()}`);
      setUsers(res.data.data?.users || []);
      setUserPagination(res.data.data?.pagination || { currentPage: 1, totalPages: 1 });
    } catch (err) {
      console.error('Users load error', err);
      toast.error('Failed to load users');
    }
  };

  const openCreateUser = () => {
    setUserForm({ name: '', email: '', password: '', role: 'user' });
    setShowUserModal(true);
  };

  const saveUser = async () => {
    try {
      await api.post('/users/admin/users', userForm);
      toast.success('User created');
      setShowUserModal(false);
      fetchUsers(1);
    } catch (err) {
      console.error('Create user error', err);
      toast.error(err.response?.data?.message || 'Failed to create user');
    }
  };

  const activateUser = async (id) => {
    try {
      setUserBusyId(id);
      await api.put(`/users/admin/users/${id}/activate`);
      toast.success('User activated');
      fetchUsers();
    } catch (err) {
      console.error('Activate user error', err);
      toast.error('Failed to activate');
    } finally {
      setUserBusyId(null);
    }
  };

  const deactivateUser = async (id) => {
    const reason = prompt('Reason for deactivation (optional):') || '';
    try {
      setUserBusyId(id);
      await api.put(`/users/admin/users/${id}/deactivate`, { reason });
      toast.success('User deactivated');
      fetchUsers();
    } catch (err) {
      console.error('Deactivate user error', err);
      toast.error('Failed to deactivate');
    } finally {
      setUserBusyId(null);
    }
  };

  const deleteUser = async (id) => {
    if (!window.confirm('Delete this user? This cannot be undone.')) return;
    try {
      setUserBusyId(id);
      await api.delete(`/users/admin/users/${id}`);
      toast.success('User deleted');
      fetchUsers();
    } catch (err) {
      console.error('Delete user error', err);
      toast.error(err.response?.data?.message || 'Failed to delete user');
    } finally {
      setUserBusyId(null);
    }
  };

  const changeUserRole = async (id, newRole) => {
    try {
      setUserBusyId(id);
      await api.put(`/users/admin/users/${id}/role`, { newRole });
      toast.success('Role updated');
      fetchUsers();
    } catch (err) {
      console.error('Change role error', err);
      toast.error('Failed to update role');
    } finally {
      setUserBusyId(null);
    }
  };

  // Theaters admin
  const fetchAllTheaters = async (page = theaterPagination.currentPage) => {
    try {
      const params = new URLSearchParams();
      if (theaterFilters.search) params.set('search', theaterFilters.search);
      if (theaterFilters.city) params.set('city', theaterFilters.city);
      params.set('page', page);
      params.set('limit', 10);
      const res = await api.get(`/theaters/admin/all?${params.toString()}`);
      setAllTheaters(res.data.data?.theaters || []);
      setTheaterPagination(res.data.data?.pagination || { currentPage: 1, totalPages: 1 });
    } catch (err) {
      console.error('Theaters load error', err);
      toast.error('Failed to load theaters');
    }
  };

  const openCreateTheater = () => {
    setEditingTheater(null);
    setTheaterForm({
      name: '',
      address: { street: '', city: '', state: '', pincode: '' },
      location: { type: 'Point', coordinates: ['', ''] },
      facilities: [],
      description: ''
    });
    setShowTheaterModal(true);
  };

  const openEditTheater = (t) => {
    setEditingTheater(t);
    setTheaterForm({
      name: t.name || '',
      address: t.address || { street: '', city: '', state: '', pincode: '' },
      location: t.location || { type: 'Point', coordinates: ['', ''] },
      facilities: t.facilities || [],
      description: t.description || ''
    });
    setShowTheaterModal(true);
  };

  const saveTheater = async () => {
    const body = {
      ...theaterForm,
      location: {
        type: 'Point',
        coordinates: [Number(theaterForm.location.coordinates[0]), Number(theaterForm.location.coordinates[1])]
      }
    };
    try {
      if (editingTheater) {
        await api.put(`/theaters/admin/${editingTheater._id}`, body);
        toast.success('Theater updated');
      } else {
        await api.post('/theaters/admin', body);
        toast.success('Theater created');
      }
      setShowTheaterModal(false);
      fetchAllTheaters(1);
    } catch (err) {
      console.error('Save theater error', err);
      toast.error(err.response?.data?.message || 'Failed to save theater');
    }
  };

  const deleteTheater = async (id) => {
    if (!window.confirm('Delete this theater? This cannot be undone.')) return;
    try {
      setTheaterBusyId(id);
      await api.delete(`/theaters/admin/${id}`);
      toast.success('Theater deleted');
      fetchAllTheaters();
    } catch (err) {
      console.error('Delete theater error', err);
      toast.error(err.response?.data?.message || 'Failed to delete theater');
    } finally {
      setTheaterBusyId(null);
    }
  };

  // Movies admin
  const fetchMovies = async (page = moviePagination.currentPage) => {
    try {
      const params = new URLSearchParams();
      if (movieSearch) params.set('search', movieSearch);
      params.set('page', page);
      params.set('limit', 10);
      const res = await api.get(`/movies?${params.toString()}`);
      setMovies(res.data.data?.movies || []);
      setMoviePagination(res.data.data?.pagination || { currentPage: 1, totalPages: 1 });
    } catch (err) {
      console.error('Movies load error', err);
      toast.error('Failed to load movies');
    }
  };

  const deleteMovie = async (id) => {
    if (!window.confirm('Delete this movie? It must have no upcoming shows.')) return;
    try {
      setMovieBusyId(id);
      await api.delete(`/movies/${id}`);
      toast.success('Movie deleted');
      fetchMovies();
    } catch (err) {
      console.error('Delete movie error', err);
      toast.error(err.response?.data?.message || 'Failed to delete movie');
    } finally {
      setMovieBusyId(null);
    }
  };

  // UI helpers
  const pill = (text, color = 'gray') => (
    <span className={`px-2 py-0.5 rounded-full text-xs bg-${color}-100 text-${color}-800`}>{text}</span>
  );

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 py-8">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-center items-center h-64">
            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-600"></div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Header */}
        <div className="mb-6">
          <h1 className="text-3xl font-bold text-gray-900 mb-1">Super Admin Dashboard</h1>
          <p className="text-gray-600">Welcome, {user?.name}. Manage users, theaters, and content.</p>
        </div>

        {/* Tabs */}
        <div className="mb-6 border-b">
          <nav className="-mb-px flex gap-6">
            {[
              { id: 'overview', label: 'Overview' },
              { id: 'theaters', label: 'Theaters' },
              { id: 'users', label: 'Users' },
              { id: 'movies', label: 'Movies' },
              { id: 'requests', label: 'Theater Admin Requests' } // New tab for approvals
            ].map(t => (
              <button
                key={t.id}
                onClick={() => setActiveTab(t.id)}
                className={`py-3 border-b-2 ${activeTab === t.id ? 'border-primary-600 text-primary-600' : 'border-transparent text-gray-500 hover:text-gray-700'}`}
              >
                {t.label}
              </button>
            ))}
          </nav>
        </div>

        {/* Overview */}
        {activeTab === 'overview' && (
          <>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
              <CardStat title="Total Users" value={stats.totalUsers || 0} icon={<FiUsers className="h-8 w-8 text-primary-600" />} />
              <CardStat title="Total Theaters" value={stats.totalTheaters || 0} icon={<FiMapPin className="h-8 w-8 text-blue-600" />} footer={`${pendingRequests.length} pending requests`} />
              <CardStat title="Total Movies" value={stats.totalMovies || 0} icon={<FiFilm className="h-8 w-8 text-purple-600" />} />
              <CardStat title="Total Revenue" value={`₹${(stats.totalRevenue || 0).toLocaleString()}`} icon={<FiDollarSign className="h-8 w-8 text-green-600" />} />
            </div>

            <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
              {/* Recent Activity */}
              <div className="bg-white rounded-lg shadow-sm p-6">
                <h2 className="text-lg font-semibold mb-4">Recent Activity</h2>
                {recentActivity?.length ? (
                  <div className="space-y-3">
                    {recentActivity.slice(0, 10).map((a, i) => (
                      <div key={i} className="flex items-start gap-3">
                        <div className="w-2 h-2 bg-primary-600 rounded-full mt-2"></div>
                        <div>
                          <div className="text-sm text-gray-900">{a.description}</div>
                          <div className="text-xs text-gray-500">{new Date(a.timestamp).toLocaleString()}</div>
                        </div>
                      </div>
                    ))}
                  </div>
                ) : (
                  <Empty icon={<FiTrendingUp className="h-12 w-12 text-gray-400" />} text="No recent activity" />
                )}
              </div>
            </div>
          </>
        )}

        {/* Theater Admin Requests Tab (pending theaters for approval) */}
        {activeTab === 'requests' && (
          <div className="bg-white rounded-lg shadow-sm p-6">
            <div className="flex items-center justify-between mb-4">
              <h2 className="text-lg font-semibold">Pending Theaters</h2>
              <button onClick={fetchPendingRequests} className="text-sm text-primary-600 hover:text-primary-700">Refresh</button>
            </div>
            {pendingRequests.length === 0 ? (
              <Empty icon={<FiShield className="h-12 w-12 text-gray-400" />} text="No pending requests" />
            ) : (
              <div className="overflow-x-auto">
                <table className="min-w-full divide-y divide-gray-200">
                  <thead className="bg-gray-50">
                    <tr>
                      <Th>Theater</Th>
                      <Th>City</Th>
                      <Th>Requested</Th>
                      <Th>Actions</Th>
                    </tr>
                  </thead>
                  <tbody className="bg-white divide-y divide-gray-200">
                    {pendingRequests.map(t => (
                      <tr key={t._id} className="hover:bg-gray-50">
                        <Td>
                          <div className="font-medium text-gray-900">{t.name}</div>
                          <div className="text-sm text-gray-600">{t.address?.street}</div>
                        </Td>
                        <Td>{t.address?.city}</Td>
                        <Td>{new Date(t.createdAt).toLocaleDateString()}</Td>
                        <Td>
                          <div className="flex gap-2">
                            <button onClick={() => approveRequest(t._id)} disabled={approvingId === t._id} className="px-3 py-1 bg-green-600 text-white rounded-md hover:bg-green-700">
                              {approvingId === t._id ? '...' : 'Approve'}
                            </button>
                            <button onClick={() => rejectRequest(t._id)} disabled={approvingId === t._id} className="px-3 py-1 bg-red-600 text-white rounded-md hover:bg-red-700">
                              Reject
                            </button>
                          </div>
                        </Td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            )}
          </div>
        )}

        {/* Theaters Tab (full CRUD for super admin) */}
        {activeTab === 'theaters' && (
          <div className="bg-white rounded-lg shadow-sm p-6">
            <div className="flex flex-col md:flex-row md:items-center justify-between gap-3 mb-4">
              <h2 className="text-lg font-semibold">Theaters</h2>
              <div className="flex flex-wrap gap-2">
                <div className="relative">
                  <FiSearch className="absolute left-3 top-1/2 -translate-y-1/2 text-gray-400" />
                  <input
                    value={theaterFilters.search}
                    onChange={(e) => setTheaterFilters({ ...theaterFilters, search: e.target.value })}
                    onKeyDown={(e) => e.key === 'Enter' && fetchAllTheaters(1)}
                    placeholder="Search name/city"
                    className="pl-9 pr-3 py-2 border rounded-md"
                  />
                </div>
                <input
                  value={theaterFilters.city}
                  onChange={(e) => setTheaterFilters({ ...theaterFilters, city: e.target.value })}
                  onKeyDown={(e) => e.key === 'Enter' && fetchAllTheaters(1)}
                  placeholder="City"
                  className="px-3 py-2 border rounded-md"
                />
                <button onClick={() => fetchAllTheaters(1)} className="px-3 py-2 bg-primary-600 text-white rounded-md">Apply</button>
                <button onClick={() => { setTheaterFilters({ search: '', city: '' }); fetchAllTheaters(1); }} className="px-3 py-2 border rounded-md">Reset</button>
                <button onClick={openCreateTheater} className="px-3 py-2 bg-green-600 text-white rounded-md flex items-center gap-2"><FiPlus /> Add Theater</button>
              </div>
            </div>

            {allTheaters.length === 0 ? (
              <Empty icon={<FiMapPin className="h-12 w-12 text-gray-400" />} text="No theaters found" />
            ) : (
              <div className="overflow-x-auto">
                <table className="min-w-full divide-y divide-gray-200">
                  <thead className="bg-gray-50">
                    <tr>
                      <Th>Name</Th>
                      <Th>City</Th>
                      <Th>Screens</Th>
                      <Th>Facilities</Th>
                      <Th>Actions</Th>
                    </tr>
                  </thead>
                  <tbody className="bg-white divide-y divide-gray-200">
                    {allTheaters.map(t => (
                      <tr key={t._id} className="hover:bg-gray-50">
                        <Td>
                          <div className="font-medium text-gray-900">{t.name}</div>
                          <div className="text-sm text-gray-600">{t.address?.street}, {t.address?.city}</div>
                        </Td>
                        <Td>{t.address?.city}</Td>
                        <Td>{t.screensCount ?? t.screens?.length ?? 0}</Td>
                        <Td>
                          <div className="flex flex-wrap gap-1">
                            {(t.facilities || []).slice(0, 4).map(f => <span key={f} className="px-2 py-0.5 text-xs bg-gray-100 rounded-full">{f}</span>)}
                            {(t.facilities || []).length > 4 && <span className="text-xs text-gray-500">+{t.facilities.length - 4}</span>}
                          </div>
                        </Td>
                        <Td>
                          <div className="flex gap-2">
                            <button onClick={() => openEditTheater(t)} className="px-2 py-1 bg-yellow-600 text-white rounded-md flex items-center gap-1">
                              <FiEdit /> Edit
                            </button>
                            <button onClick={() => deleteTheater(t._id)} disabled={theaterBusyId === t._id} className="px-2 py-1 bg-red-600 text-white rounded-md flex items-center gap-1">
                              <FiTrash2 /> Delete
                            </button>
                          </div>
                        </Td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            )}

            <Pager pagination={theaterPagination} onPage={(p) => fetchAllTheaters(p)} />
          </div>
        )}

        {/* Users Tab */}
        {activeTab === 'users' && (
          <div className="bg-white rounded-lg shadow-sm p-6">
            <div className="flex flex-col md:flex-row md:items-center justify-between gap-3 mb-4">
              <h2 className="text-lg font-semibold">Users</h2>
              <div className="flex flex-wrap gap-2">
                <div className="relative">
                  <FiSearch className="absolute left-3 top-1/2 -translate-y-1/2 text-gray-400" />
                  <input
                    value={userFilters.search}
                    onChange={(e) => setUserFilters({ ...userFilters, search: e.target.value })}
                    onKeyDown={(e) => e.key === 'Enter' && fetchUsers(1)}
                    placeholder="Search name/email/phone"
                    className="pl-9 pr-3 py-2 border rounded-md"
                  />
                </div>
                <select
                  value={userFilters.role}
                  onChange={(e) => setUserFilters({ ...userFilters, role: e.target.value })}
                  className="border rounded-md px-2 py-2"
                >
                  <option value="">All roles</option>
                  <option value="user">User</option>
                  <option value="theater_admin">Theater Admin</option>
                  <option value="super_admin">Super Admin</option>
                </select>
                <select
                  value={userFilters.isActive}
                  onChange={(e) => setUserFilters({ ...userFilters, isActive: e.target.value })}
                  className="border rounded-md px-2 py-2"
                >
                  <option value="">Any status</option>
                  <option value="true">Active</option>
                  <option value="false">Inactive</option>
                </select>
                <button onClick={() => fetchUsers(1)} className="px-3 py-2 bg-primary-600 text-white rounded-md">Apply</button>
                <button onClick={() => { setUserFilters({ search: '', role: '', isActive: '' }); fetchUsers(1); }} className="px-3 py-2 border rounded-md">Reset</button>
                <button onClick={openCreateUser} className="px-3 py-2 bg-green-600 text-white rounded-md flex items-center gap-2"><FiPlus /> Add User</button>
              </div>
            </div>

            {users.length === 0 ? (
              <Empty icon={<FiUsers className="h-12 w-12 text-gray-400" />} text="No users found" />
            ) : (
              <div className="overflow-x-auto">
                <table className="min-w-full divide-y divide-gray-200">
                  <thead className="bg-gray-50">
                    <tr>
                      <Th>User</Th>
                      <Th>Role</Th>
                      <Th>Status</Th>
                      <Th>Stats</Th>
                      <Th>Actions</Th>
                    </tr>
                  </thead>
                  <tbody className="bg-white divide-y divide-gray-200">
                    {users.map(u => (
                      <tr key={u._id} className="hover:bg-gray-50">
                        <Td>
                          <div className="font-medium text-gray-900">{u.name}</div>
                          <div className="text-sm text-gray-600">{u.email}</div>
                        </Td>
                        <Td>{pill(u.role === 'super_admin' ? 'Super Admin' : u.role === 'theater_admin' ? 'Theater Admin' : 'User', u.role === 'super_admin' ? 'purple' : u.role === 'theater_admin' ? 'blue' : 'gray')}</Td>
                        <Td>{pill(u.isActive ? 'Active' : 'Inactive', u.isActive ? 'green' : 'red')}</Td>
                        <Td>
                          <div className="text-sm text-gray-700">
                            {u.stats?.totalBookings || 0} bookings • ₹{(u.stats?.totalSpent || 0).toLocaleString()}
                          </div>
                        </Td>
                        <Td>
                          <div className="flex flex-wrap gap-2">
                            {u.isActive ? (
                              <button onClick={() => deactivateUser(u._id)} disabled={userBusyId === u._id} className="px-2 py-1 bg-yellow-600 text-white rounded-md flex items-center gap-1">
                                <FiUserMinus /> Deactivate
                              </button>
                            ) : (
                              <button onClick={() => activateUser(u._id)} disabled={userBusyId === u._id} className="px-2 py-1 bg-green-600 text-white rounded-md">
                                Activate
                              </button>
                            )}
                            <RoleDropdown user={u} onChange={(r) => changeUserRole(u._id, r)} busy={userBusyId === u._id} />
                            <button onClick={() => deleteUser(u._id)} disabled={userBusyId === u._id} className="px-2 py-1 bg-red-600 text-white rounded-md flex items-center gap-1">
                              <FiTrash2 /> Delete
                            </button>
                          </div>
                        </Td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            )}

            <Pager pagination={userPagination} onPage={(p) => fetchUsers(p)} />
          </div>
        )}

        {/* Movies Tab */}
        {activeTab === 'movies' && (
          <div className="bg-white rounded-lg shadow-sm p-6">
            <div className="flex flex-col md:flex-row md:items-center justify-between gap-3 mb-4">
              <h2 className="text-lg font-semibold">Movies</h2>
              <div className="flex gap-2">
                <div className="relative">
                  <FiSearch className="absolute left-3 top-1/2 -translate-y-1/2 text-gray-400" />
                  <input
                    value={movieSearch}
                    onChange={(e) => setMovieSearch(e.target.value)}
                    onKeyDown={(e) => e.key === 'Enter' && fetchMovies(1)}
                    placeholder="Search movies..."
                    className="pl-9 pr-3 py-2 border rounded-md"
                  />
                </div>
                <button onClick={() => fetchMovies(1)} className="px-3 py-2 bg-primary-600 text-white rounded-md">Search</button>
              </div>
            </div>

            {movies.length === 0 ? (
              <Empty icon={<FiFilm className="h-12 w-12 text-gray-400" />} text="No movies found" />
            ) : (
              <div className="overflow-x-auto">
                <table className="min-w-full divide-y divide-gray-200">
                  <thead className="bg-gray-50">
                    <tr>
                      <Th>Title</Th>
                      <Th>Language</Th>
                      <Th>Rating</Th>
                      <Th>Has Shows</Th>
                      <Th>Actions</Th>
                    </tr>
                  </thead>
                  <tbody className="bg-white divide-y divide-gray-200">
                    {movies.map(m => (
                      <tr key={m._id} className="hover:bg-gray-50">
                        <Td>
                          <div className="font-medium text-gray-900">{m.title}</div>
                          <div className="text-sm text-gray-600">{m.genre?.slice(0, 3).join(', ')}</div>
                        </Td>
                        <Td>{m.language}</Td>
                        <Td>{m.imdbRating ?? '—'}</Td>
                        <Td>{pill(m.hasUpcomingShows ? 'Yes' : 'No', m.hasUpcomingShows ? 'green' : 'gray')}</Td>
                        <Td>
                          <button
                            onClick={() => deleteMovie(m._id)}
                            disabled={movieBusyId === m._id}
                            className="px-2 py-1 bg-red-600 text-white rounded-md flex items-center gap-1"
                          >
                            <FiTrash2 /> Delete
                          </button>
                        </Td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            )}

            <Pager pagination={moviePagination} onPage={(p) => fetchMovies(p)} />
          </div>
        )}

        {/* User Modal */}
        {showUserModal && (
          <div className="fixed inset-0 bg-black/40 flex items-center justify-center p-4 z-50">
            <div className="bg-white rounded-lg w-full max-w-md p-6">
              <div className="flex items-center justify-between mb-4">
                <h3 className="text-lg font-semibold">Add User</h3>
                <button onClick={() => setShowUserModal(false)} className="text-gray-500">✕</button>
              </div>
              <div className="space-y-3">
                <input className="border rounded px-3 py-2 w-full" placeholder="Name" value={userForm.name} onChange={(e) => setUserForm({ ...userForm, name: e.target.value })} />
                <input className="border rounded px-3 py-2 w-full" placeholder="Email" type="email" value={userForm.email} onChange={(e) => setUserForm({ ...userForm, email: e.target.value })} />
                <input className="border rounded px-3 py-2 w-full" placeholder="Password" type="password" value={userForm.password} onChange={(e) => setUserForm({ ...userForm, password: e.target.value })} />
                <select className="border rounded px-3 py-2 w-full" value={userForm.role} onChange={(e) => setUserForm({ ...userForm, role: e.target.value })}>
                  <option value="user">User</option>
                  <option value="theater_admin">Theater Admin</option>
                  <option value="super_admin">Super Admin</option>
                </select>
              </div>
              <div className="flex justify-end gap-2 mt-4">
                <button onClick={() => setShowUserModal(false)} className="px-3 py-2 border rounded">Cancel</button>
                <button onClick={saveUser} className="px-3 py-2 bg-primary-600 text-white rounded">Create</button>
              </div>
            </div>
          </div>
        )}

        {/* Theater Modal */}
        {showTheaterModal && (
          <div className="fixed inset-0 bg-black/40 flex items-center justify-center p-4 z-50">
            <div className="bg-white rounded-lg w-full max-w-2xl p-6">
              <div className="flex items-center justify-between mb-4">
                <h3 className="text-lg font-semibold">{editingTheater ? 'Edit Theater' : 'Add Theater'}</h3>
                <button onClick={() => setShowTheaterModal(false)} className="text-gray-500">✕</button>
              </div>

              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <input className="border rounded px-3 py-2" placeholder="Name" value={theaterForm.name} onChange={(e) => setTheaterForm({ ...theaterForm, name: e.target.value })} />
                <input className="border rounded px-3 py-2" placeholder="Street" value={theaterForm.address.street} onChange={(e) => setTheaterForm({ ...theaterForm, address: { ...theaterForm.address, street: e.target.value } })} />
                <input className="border rounded px-3 py-2" placeholder="City" value={theaterForm.address.city} onChange={(e) => setTheaterForm({ ...theaterForm, address: { ...theaterForm.address, city: e.target.value } })} />
                <input className="border rounded px-3 py-2" placeholder="State" value={theaterForm.address.state} onChange={(e) => setTheaterForm({ ...theaterForm, address: { ...theaterForm.address, state: e.target.value } })} />
                <input className="border rounded px-3 py-2" placeholder="Pincode" value={theaterForm.address.pincode} onChange={(e) => setTheaterForm({ ...theaterForm, address: { ...theaterForm.address, pincode: e.target.value } })} />
                <input className="border rounded px-3 py-2" placeholder="Longitude" value={theaterForm.location.coordinates[0]} onChange={(e) => setTheaterForm({ ...theaterForm, location: { ...theaterForm.location, coordinates: [e.target.value, theaterForm.location.coordinates[1]] } })} />
                <input className="border rounded px-3 py-2" placeholder="Latitude" value={theaterForm.location.coordinates[1]} onChange={(e) => setTheaterForm({ ...theaterForm, location: { ...theaterForm.location, coordinates: [theaterForm.location.coordinates[0], e.target.value] } })} />
                <input className="border rounded px-3 py-2 md:col-span-2" placeholder="Facilities (comma-separated)" value={(theaterForm.facilities || []).join(', ')} onChange={(e) => setTheaterForm({ ...theaterForm, facilities: e.target.value.split(',').map(s => s.trim()).filter(Boolean) })} />
                <textarea className="border rounded px-3 py-2 md:col-span-2" placeholder="Description" rows={3} value={theaterForm.description} onChange={(e) => setTheaterForm({ ...theaterForm, description: e.target.value })} />
              </div>

              <div className="flex justify-end gap-2 mt-4">
                <button onClick={() => setShowTheaterModal(false)} className="px-3 py-2 border rounded">Cancel</button>
                <button onClick={saveTheater} className="px-3 py-2 bg-primary-600 text-white rounded">{editingTheater ? 'Save' : 'Create'}</button>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

// UI helpers (keep as is from previous)
const CardStat = ({ title, value, icon, footer }) => (
  <div className="bg-white rounded-lg shadow-sm p-6">
    <div className="flex items-center">
      <div className="flex-1">
        <p className="text-sm font-medium text-gray-600">{title}</p>
        <p className="text-3xl font-bold text-gray-900">{value}</p>
      </div>
      <div className="ml-4">{icon}</div>
    </div>
    {footer && <div className="mt-2 text-sm text-gray-600">{footer}</div>}
  </div>
);

const Empty = ({ icon, text }) => (
  <div className="text-center py-10">
    <div className="mb-3 flex justify-center">{icon}</div>
    <p className="text-gray-500">{text}</p>
  </div>
);

const Th = ({ children }) => (
  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">{children}</th>
);
const Td = ({ children }) => (
  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-700">{children}</td>
);

const Pager = ({ pagination, onPage }) => {
  const { currentPage, totalPages, hasPrev, hasNext } = pagination || {};
  if (!totalPages || totalPages <= 1) return null;
  return (
    <div className="flex justify-center items-center gap-2 mt-4">
      <button disabled={!hasPrev} onClick={() => onPage(currentPage - 1)} className="px-3 py-2 border rounded disabled:opacity-50">Prev</button>
      <span className="text-sm text-gray-600">Page {currentPage} of {totalPages}</span>
      <button disabled={!hasNext} onClick={() => onPage(currentPage + 1)} className="px-3 py-2 border rounded disabled:opacity-50">Next</button>
    </div>
  );
};

const RoleDropdown = ({ user, onChange, busy }) => {
  const [value, setValue] = useState(user.role);
  return (
    <select
      value={value}
      disabled={busy}
      onChange={(e) => { setValue(e.target.value); onChange(e.target.value); }}
      className="border rounded-md px-2 py-1 text-sm"
    >
      <option value="user">User</option>
      <option value="theater_admin">Theater Admin</option>
      <option value="super_admin">Super Admin</option>
    </select>
  );
};

export default SuperAdminDashboard;
