import React, { useState, useEffect } from 'react';
import { FiUsers, FiFilm, FiHome, FiBarChart3, FiCheckCircle, FiXCircle } from 'react-icons/fi';
import api from '..././services/api';

const SuperAdminDashboard = () => {
  const [stats, setStats] = useState({
    totalUsers: 0,
    totalTheaters: 0,
    pendingApprovals: 0,
    totalBookings: 0,
  });
  const [pendingTheaters, setPendingTheaters] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      // Fetch stats
      const [usersRes, theatersRes, bookingsRes] = await Promise.all([
        api.get('/users'),
        api.get('/theaters'),
        api.get('/bookings')
      ]);

      const theaters = theatersRes.data.data;
      const pending = theaters.filter(t => t.status === 'pending');

      setStats({
        totalUsers: usersRes.data.data.length,
        totalTheaters: theaters.length,
        pendingApprovals: pending.length,
        totalBookings: bookingsRes.data.data.length,
      });
      setPendingTheaters(pending);
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleTheaterApproval = async (theaterId, action) => {
    try {
      await api.put(`/theaters/${theaterId}/status`, { status: action });
      toast.success(`Theater ${action === 'approved' ? 'approved' : 'rejected'} successfully`);
      fetchDashboardData();
    } catch (error) {
      toast.error('Error updating theater status');
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-dark-900 flex items-center justify-center">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-primary-500"></div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-dark-900">
      {/* Header */}
      <div className="bg-gradient-to-r from-primary-600 to-secondary-600 py-8">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <h1 className="text-3xl font-bold text-white">Super Admin Dashboard</h1>
          <p className="text-primary-100 mt-2">Manage the entire MovieBook platform</p>
        </div>
      </div>

      {/* Stats Grid */}
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
          <div className="bg-dark-800 rounded-2xl p-6">
            <div className="flex items-center">
              <div className="p-3 bg-primary-500/20 rounded-lg">
                <FiUsers className="h-6 w-6 text-primary-500" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-dark-400">Total Users</p>
                <p className="text-2xl font-bold text-white">{stats.totalUsers}</p>
              </div>
            </div>
          </div>

          <div className="bg-dark-800 rounded-2xl p-6">
            <div className="flex items-center">
              <div className="p-3 bg-secondary-500/20 rounded-lg">
                <FiHome className="h-6 w-6 text-secondary-500" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-dark-400">Total Theaters</p>
                <p className="text-2xl font-bold text-white">{stats.totalTheaters}</p>
              </div>
            </div>
          </div>

          <div className="bg-dark-800 rounded-2xl p-6">
            <div className="flex items-center">
              <div className="p-3 bg-yellow-500/20 rounded-lg">
                <FiBarChart3 className="h-6 w-6 text-yellow-500" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-dark-400">Pending Approvals</p>
                <p className="text-2xl font-bold text-white">{stats.pendingApprovals}</p>
              </div>
            </div>
          </div>

          <div className="bg-dark-800 rounded-2xl p-6">
            <div className="flex items-center">
              <div className="p-3 bg-green-500/20 rounded-lg">
                <FiFilm className="h-6 w-6 text-green-500" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-dark-400">Total Bookings</p>
                <p className="text-2xl font-bold text-white">{stats.totalBookings}</p>
              </div>
            </div>
          </div>
        </div>

        {/* Pending Approvals */}
        <div className="bg-dark-800 rounded-2xl p-6">
          <h2 className="text-xl font-bold text-white mb-6">Pending Theater Approvals</h2>
          
          {pendingTheaters.length === 0 ? (
            <p className="text-dark-400 text-center py-8">No pending approvals</p>
          ) : (
            <div className="space-y-4">
              {pendingTheaters.map((theater) => (
                <div key={theater._id} className="flex items-center justify-between p-4 bg-dark-700 rounded-lg">
                  <div>
                    <h3 className="font-semibold text-white">{theater.name}</h3>
                    <p className="text-dark-400 text-sm">{theater.address.city}, {theater.address.state}</p>
                    <p className="text-dark-400 text-sm">{theater.contact.email} | {theater.contact.phone}</p>
                  </div>
                  <div className="flex space-x-2">
                    <button
                      onClick={() => handleTheaterApproval(theater._id, 'approved')}
                      className="flex items-center px-4 py-2 bg-green-500 text-white rounded-lg hover:bg-green-600 transition-colors"
                    >
                      <FiCheckCircle className="mr-2" />
                      Approve
                    </button>
                    <button
                      onClick={() => handleTheaterApproval(theater._id, 'rejected')}
                      className="flex items-center px-4 py-2 bg-red-500 text-white rounded-lg hover:bg-red-600 transition-colors"
                    >
                      <FiXCircle className="mr-2" />
                      Reject
                    </button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default SuperAdminDashboard;
