import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { FiCalendar, FiClock, FiStar, FiSearch } from 'react-icons/fi';
import api from '../services/api';

const Movies = () => {
  const [movies, setMovies] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    fetchMovies();
  }, []);

  const fetchMovies = async () => {
    try {
      const response = await api.get('/movies');
      setMovies(response.data.data);
    } catch (error) {
      console.error('Error fetching movies:', error);
    } finally {
      setLoading(false);
    }
  };

  const filteredMovies = movies.filter(movie =>
    movie.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
    movie.genre.some(g => g.toLowerCase().includes(searchTerm.toLowerCase()))
  );

  if (loading) {
    return (
      <div className="min-h-screen bg-dark-900 flex items-center justify-center">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-primary-500"></div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-dark-900 py-8">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Header */}
        <div className="text-center mb-8">
          <h1 className="text-4xl font-bold text-white mb-4">Now Showing</h1>
          <p className="text-dark-400 text-lg">Discover amazing movies and book your tickets</p>
        </div>

        {/* Search Bar */}
        <div className="mb-8">
          <div className="relative max-w-2xl mx-auto">
            <FiSearch className="absolute left-4 top-1/2 transform -translate-y-1/2 text-dark-400 h-5 w-5" />
            <input
              type="text"
              placeholder="Search movies by title or genre..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="w-full pl-12 pr-4 py-3 bg-dark-800 border border-dark-700 rounded-lg text-white placeholder-dark-400 focus:outline-none focus:ring-2 focus:ring-primary-500 focus:border-transparent"
            />
          </div>
        </div>

        {/* Movies Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
          {filteredMovies.map((movie) => (
            <div key={movie._id} className="bg-dark-800 rounded-2xl overflow-hidden shadow-lg hover:shadow-xl transition-shadow duration-300">
              <img
                src={movie.posterUrl}
                alt={movie.title}
                className="w-full h-64 object-cover"
              />
              <div className="p-6">
                <h3 className="text-xl font-bold text-white mb-2 line-clamp-1">{movie.title}</h3>
                
                <div className="flex items-center text-dark-400 text-sm mb-3">
                  <FiCalendar className="mr-1 h-4 w-4" />
                  {new Date(movie.releaseDate).getFullYear()}
                  <span className="mx-2">â€¢</span>
                  <FiClock className="mr-1 h-4 w-4" />
                  {Math.floor(movie.duration / 60)}h {movie.duration % 60}m
                </div>

                <div className="flex flex-wrap gap-2 mb-4">
                  {movie.genre.slice(0, 3).map((genre) => (
                    <span key={genre} className="px-2 py-1 bg-primary-500/20 text-primary-300 rounded text-xs">
                      {genre}
                    </span>
                  ))}
                </div>

                <p className="text-dark-400 text-sm mb-4 line-clamp-2">{movie.description}</p>

                <div className="flex items-center justify-between">
                  <div className="flex items-center">
                    <FiStar className="text-secondary-500 mr-1" />
                    <span className="text-white font-semibold">{movie.imdbRating || 'N/A'}</span>
                  </div>
                  <Link
                    to={`/movie/${movie._id}`}
                    className="bg-gradient-to-r from-primary-500 to-secondary-500 text-white px-4 py-2 rounded-lg text-sm font-semibold hover:from-primary-600 hover:to-secondary-600 transition-all duration-200"
                  >
                    Book Now
                  </Link>
                </div>
              </div>
            </div>
          ))}
        </div>

        {filteredMovies.length === 0 && (
          <div className="text-center py-12">
            <p className="text-dark-400 text-lg">No movies found matching your search.</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Movies;
