import React, { useState, useEffect } from 'react';
import { FiFilm, FiCalendar, FiDollarSign, FiUsers, FiPlus, FiEdit, FiTrash2 } from 'react-icons/fi';
import api from '../../services/api';
import { toast } from 'react-hot-toast';
import { useAuth } from '../../context/AuthContext';

const TheaterAdminDashboard = () => {
  const { user } = useAuth();
  const [stats, setStats] = useState({
    totalShows: 0,
    totalBookings: 0,
    totalRevenue: 0,
    todayBookings: 0
  });
  const [shows, setShows] = useState([]);
  const [movies, setMovies] = useState([]);
  const [movieModalOpen, setMovieModalOpen] = useState(false);
  const [movieForm, setMovieForm] = useState({
    title: '',
    genre: '',
    language: '',
    duration: '',
    description: '',
    director: '',
    cast: '',
    releaseDate: '',
    posterUrl: '',
    trailerUrl: '',
    rating: 'U/A',
    imdbRating: ''
  });
  const [loading, setLoading] = useState(true);
  const [showModal, setShowModal] = useState(false);
  const [selectedShow, setSelectedShow] = useState(null);
  const [showForm, setShowForm] = useState({
    movieId: '',
    date: '',
    time: '',
    screen: '',
    price: '',
  });

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      const [statsRes, showsRes, moviesRes] = await Promise.all([
        api.get('/admin/theater/stats'),
        api.get('/admin/theater/shows'),
        api.get('/movies')
      ]);

      setStats(statsRes.data.data || {});
      setShows(showsRes.data.data?.shows || []);
      setMovies(moviesRes.data.data?.movies || []);
    } catch (error) {
      toast.error('Failed to fetch dashboard data');
      console.error('Error fetching dashboard data:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleAddShow = async (e) => {
    e.preventDefault();
    try {
      await api.post('/admin/theater/shows', showForm);
      toast.success('Show added successfully');
      setShowModal(false);
      resetForm();
      fetchDashboardData();
    } catch (error) {
      toast.error(error.response?.data?.message || 'Failed to add show');
      console.error('Error adding show:', error);
    }
  };

  const handleEditShow = (show) => {
    setSelectedShow(show);
    setShowForm({
      movieId: show.movie._id,
      date: show.date,
      time: show.time,
      screen: show.screen,
      price: show.price,
    });
    setShowModal(true);
  };

  const handleUpdateShow = async (e) => {
    e.preventDefault();
    try {
      await api.put(`/admin/theater/shows/${selectedShow._id}`, showForm);
      toast.success('Show updated successfully');
      setShowModal(false);
      resetForm();
      fetchDashboardData();
    } catch (error) {
      toast.error(error.response?.data?.message || 'Failed to update show');
      console.error('Error updating show:', error);
    }
  };

  const handleDeleteShow = async (showId) => {
    if (!window.confirm('Are you sure you want to delete this show?')) {
      return;
    }

    try {
      await api.delete(`/admin/theater/shows/${showId}`);
      toast.success('Show deleted successfully');
      fetchDashboardData();
    } catch (error) {
      toast.error('Failed to delete show');
      console.error('Error deleting show:', error);
    }
  };

  const resetForm = () => {
    setShowForm({
      movieId: '',
      date: '',
      time: '',
      screen: '',
      price: '',
    });
    setSelectedShow(null);
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      weekday: 'short',
      month: 'short',
      day: 'numeric'
    });
  };

  const formatTime = (timeString) => {
    const time = new Date(`2000-01-01 ${timeString}`);
    return time.toLocaleTimeString('en-US', {
      hour: '2-digit',
      minute: '2-digit',
      hour12: true
    });
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 py-8">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-center items-center h-64">
            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-600"></div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Header */}
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900 mb-2">Theater Admin Dashboard</h1>
          <p className="text-gray-600">Welcome back, {user?.name}! Manage your theater shows and bookings.</p>
        </div>

        {/* Stats Cards */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
          <div className="bg-white rounded-lg shadow-sm p-6">
            <div className="flex items-center">
              <div className="flex-1">
                <p className="text-sm font-medium text-gray-600">Total Shows</p>
                <p className="text-3xl font-bold text-gray-900">{stats.totalShows || 0}</p>
              </div>
              <div className="ml-4">
                <FiFilm className="h-8 w-8 text-primary-600" />
              </div>
            </div>
            <div className="mt-2">
              <span className="text-sm text-primary-600">Active shows</span>
            </div>
          </div>

          <div className="bg-white rounded-lg shadow-sm p-6">
            <div className="flex items-center">
              <div className="flex-1">
                <p className="text-sm font-medium text-gray-600">Total Bookings</p>
                <p className="text-3xl font-bold text-gray-900">{stats.totalBookings || 0}</p>
              </div>
              <div className="ml-4">
                <FiUsers className="h-8 w-8 text-blue-600" />
              </div>
            </div>
            <div className="mt-2">
              <span className="text-sm text-blue-600">All time bookings</span>
            </div>
          </div>

          <div className="bg-white rounded-lg shadow-sm p-6">
            <div className="flex items-center">
              <div className="flex-1">
                <p className="text-sm font-medium text-gray-600">Today's Bookings</p>
                <p className="text-3xl font-bold text-gray-900">{stats.todayBookings || 0}</p>
              </div>
              <div className="ml-4">
                <FiCalendar className="h-8 w-8 text-green-600" />
              </div>
            </div>
            <div className="mt-2">
              <span className="text-sm text-green-600">Today only</span>
            </div>
          </div>

          <div className="bg-white rounded-lg shadow-sm p-6">
            <div className="flex items-center">
              <div className="flex-1">
                <p className="text-sm font-medium text-gray-600">Total Revenue</p>
                <p className="text-3xl font-bold text-gray-900">₹{(stats.totalRevenue || 0).toLocaleString()}</p>
              </div>
              <div className="ml-4">
                <FiDollarSign className="h-8 w-8 text-purple-600" />
              </div>
            </div>
            <div className="mt-2">
              <span className="text-sm text-purple-600">All time revenue</span>
            </div>
          </div>
        </div>

        {/* Shows Management */}
        <div className="bg-white rounded-lg shadow-sm p-6">
          <div className="flex items-center justify-between mb-6">
            <h2 className="text-xl font-semibold text-gray-900">Manage Shows</h2>
            <button
              onClick={() => {
                resetForm();
                setShowModal(true);
              }}
              className="flex items-center gap-2 px-4 py-2 bg-primary-600 text-white rounded-md hover:bg-primary-700 transition-colors"
            >
              <FiPlus className="w-4 h-4" />
              Add New Show
            </button>
          </div>

          {shows.length === 0 ? (
            <div className="text-center py-12">
              <FiFilm className="mx-auto h-12 w-12 text-gray-400 mb-4" />
              <h3 className="text-lg font-medium text-gray-900 mb-2">No shows scheduled</h3>
              <p className="text-gray-500 mb-4">Start by adding your first show</p>
              <button
                onClick={() => {
                  resetForm();
                  setShowModal(true);
                }}
                className="px-4 py-2 bg-primary-600 text-white rounded-md hover:bg-primary-700 transition-colors"
              >
                Add First Show
              </button>
            </div>
          ) : (
            <div className="overflow-x-auto">
              <table className="min-w-full divide-y divide-gray-200">
                <thead className="bg-gray-50">
                  <tr>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Movie
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Date & Time
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Screen
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Price
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Bookings
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Actions
                    </th>
                  </tr>
                </thead>
                <tbody className="bg-white divide-y divide-gray-200">
                  {shows.map((show) => (
                    <tr key={show._id}>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <div>
                          <div className="text-sm font-medium text-gray-900">
                            {show.movie.title}
                          </div>
                          <div className="text-sm text-gray-500">
                            {show.movie.language} • {show.movie.genre}
                          </div>
                        </div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <div className="text-sm text-gray-900">{formatDate(show.date)}</div>
                        <div className="text-sm text-gray-500">{formatTime(show.time)}</div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                        Screen {show.screen}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                        ₹{show.price}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                        {show.bookings || 0}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                        <div className="flex items-center gap-2">
                          <button
                            onClick={() => handleEditShow(show)}
                            className="text-primary-600 hover:text-primary-900"
                          >
                            <FiEdit className="w-4 h-4" />
                          </button>
                          <button
                            onClick={() => handleDeleteShow(show._id)}
                            className="text-red-600 hover:text-red-900"
                          >
                            <FiTrash2 className="w-4 h-4" />
                          </button>
                        </div>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </div>

        {/* Add/Edit Show Modal */}
        {showModal && (
          <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50">
            <div className="relative top-20 mx-auto p-5 border w-96 shadow-lg rounded-md bg-white">
              <div className="mt-3">
                <h3 className="text-lg font-medium text-gray-900 mb-4">
                  {selectedShow ? 'Edit Show' : 'Add New Show'}
                </h3>
                <form onSubmit={selectedShow ? handleUpdateShow : handleAddShow}>
                  <div className="space-y-4">
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">
                        Movie
                      </label>
                      <select
                        value={showForm.movieId}
                        onChange={(e) => setShowForm({ ...showForm, movieId: e.target.value })}
                        className="w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-primary-500 focus:border-primary-500"
                        required
                      >
                        <option value="">Select a movie</option>
                        {movies.map((movie) => (
                          <option key={movie._id} value={movie._id}>
                            {movie.title}
                          </option>
                        ))}
                      </select>
                    </div>

                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">
                        Date
                      </label>
                      <input
                        type="date"
                        value={showForm.date}
                        onChange={(e) => setShowForm({ ...showForm, date: e.target.value })}
                        className="w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-primary-500 focus:border-primary-500"
                        required
                      />
                    </div>

                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">
                        Time
                      </label>
                      <input
                        type="time"
                        value={showForm.time}
                        onChange={(e) => setShowForm({ ...showForm, time: e.target.value })}
                        className="w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-primary-500 focus:border-primary-500"
                        required
                      />
                    </div>

                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">
                        Screen
                      </label>
                      <input
                        type="number"
                        value={showForm.screen}
                        onChange={(e) => setShowForm({ ...showForm, screen: e.target.value })}
                        className="w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-primary-500 focus:border-primary-500"
                        min="1"
                        required
                      />
                    </div>

                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">
                        Price (₹)
                      </label>
                      <input
                        type="number"
                        value={showForm.price}
                        onChange={(e) => setShowForm({ ...showForm, price: e.target.value })}
                        className="w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-primary-500 focus:border-primary-500"
                        min="0"
                        required
                      />
                    </div>
                  </div>

                  <div className="flex justify-end gap-3 mt-6">
                    <button
                      type="button"
                      onClick={() => {
                        setShowModal(false);
                        resetForm();
                      }}
                      className="px-4 py-2 text-gray-700 border border-gray-300 rounded-md hover:bg-gray-50 transition-colors"
                    >
                      Cancel
                    </button>
                    <button
                      type="submit"
                      className="px-4 py-2 bg-primary-600 text-white rounded-md hover:bg-primary-700 transition-colors"
                    >
                      {selectedShow ? 'Update Show' : 'Add Show'}
                    </button>
                  </div>
                </form>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default TheaterAdminDashboard;
